set data=tHttpResponse.Data
IF $ISOBJECT(data) {
set obj=[].%FromJSON(data) set resp=##class(RFT.ERS.Classes.WorklistResponse).%New() set entry=obj.entry
IF $ISOBJECT(entry)
{
set entryIterator=entry.%GetIterator() while entryIterator.%GetNext(.key,.entryItem)
{ set ref=##class(RFT.ERS.Classes.WorklistItem).%New() set ubrn=entryItem.item.reference set ref.ubrn=$PIECE(ubrn,"/",2) set outerExtensionArray=entryItem.extension
set extensionObject=outerExtensionArray.extension
set innerExtensionArray=extensionObject.extension set innerExtensionIterator=innerExtensionArray.%GetIterator() while innerExtensionIterator.%GetNext(.key,.extensionItem) {
if extensionItem.url="priority"
{ set code=##class(RFT.Utility.CodeableConcept).RetriveCode(extensionItem) set ref.priority=code } if extensionItem.url="specialty"
{ set code=##class(RFT.Utility.CodeableConcept).RetriveCode(extensionItem) set ref.specialty=code
}
if extensionItem.url="clinicalInfoPrinted" set ref.clinicalInfoPrinted=extensionItem.valueBoolean if extensionItem.url="clinicalInfoLastUpdated" set ref.clinicalInfoLastUpdate=extensionItem.valueDateTime if extensionItem.url="patient" set ref.patientID=extensionItem.valueReference.identifier.value
}
do resp.Item.Insert(ref) }
}
set pResponse=resp }
END quit tsc

